import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import { useParams } from "react-router-dom";

function PhoneticsSamples() {
  const { REACT_APP_HOST } = process.env;
  const { phonetic_name } = useParams();
  const [Phonetics, SetPhonetics] = useState([]);
  const types = ['i', 'm', 'f'];
  function checkType(type) {
    if (type=='i'){
        return (
            <>{phonetic_name}<span style={{paddingLeft:'5px',fontWeight:'bolder'}}>&#8212;</span></>
        )
    } 
    if (type=='m'){
        return (
            <>
            <span style={{paddingLeft:'5px',fontWeight:'bolder'}}>&#8212;</span>
            {phonetic_name}
            <span style={{paddingLeft:'5px',fontWeight:'bolder'}}>&#8212;</span>
            </>
        )
    } 
  }
  const typeList = types.map((type, i) => {   
    return(
        <>
         <div className="col-md-2">
            <Link
                to={`/examples/${phonetic_name}${type}`}
                className="button-50"
                role="button"
            >
             checkType(type)
            </Link>
            </div>
        </>
    )
  });
  return (
    <div className="midde_cont">
      <div className="container-fluid">
      <div className="row column_title">
          <div className="col-md-12">
            <div className="page_title">
            <h2>Phonetics Samples for Letter {phonetic_name}</h2>
            </div>
          </div>
        </div>
 
        <div className="row column1">
          
            <div className="custom-margin"></div>
        </div>
      </div>
    </div>
  );
}
export default PhoneticsSamples;
