import React, { useState, useEffect } from "react";
import axios from "axios";
import LettersList from "./LettersList";
import { Button, Modal } from "react-bootstrap";
function PhoneticsList() {
  const { REACT_APP_HOST } = process.env;
    // #Open and close model
    const handleClose = () => setshow(false);
    const handleShow = () => setshow(true);
    const [show, setshow] = useState(false);
  return (
    <div className="midde_cont">
      <div className="container-fluid">
        <div className="row column_title page_title">
          <div className="col-6">
            <div className="">
              <h2>Phonetics List</h2>
            </div>
          </div>
          <div className="col-6 d-flex justify-content-end">
          <Button
                type="button"
                style={{background:'none',border:'none',color:'#15283c'}}
                onClick={() => {
                  handleShow();
                }}
              >
              <img
                  src="http://localhost:3000/images/icons/plus.png"
                />
              <span style={{paddingLeft:'5px'}}>Add Phonetics</span>
              </Button>
          </div>
        </div>
        <div className="row column1">
          <LettersList/>
        </div>
        <div className="row">
        <Modal show={show} onHide={handleClose}>
          <Modal.Header closeButton>
            <Modal.Title>
              <h6>إضافة مقال</h6>
            </Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <form onSubmit={handleSubmit}>
              <div className="input-group mb-3">
                <span className="input-group-text" id="basic-addon1">
                  <i className="fas fa-signature"></i>
                </span>
                <input
                  value={title}
                  onChange={(evt) => setname(evt.target.value)}
                  type="text"
                  className="form-control"
                  placeholder="Add Phonetics"
                />
              </div>

              <div className="input-group mb-3">
                <span className="input-group-text">
                  <i className="fa-solid fa-file-lock"></i>
                </span>
                <select
                  value={status}
                  onChange={(evt) => setstatus(evt.target.value)}
                  className="form-select"
                  name="problem"
                >
                  <option selected>اختر حالة المقال </option>
                  {selectStatus}
                </select>
              </div>
              <Modal.Footer>
                <Button
                  type="submit"
                  style={{
                    color: "#fff",
                    background: "#26306a",
                    border: "none",
                  }}
                  onClick={() => {
                    handleClose();
                  }}
                >
                  حفظ المقال
                </Button>
              </Modal.Footer>
            </form>
          </Modal.Body>
        </Modal>
        </div>
      </div>
    </div>
  );
}
export default PhoneticsList;
